const { stripIndents } = require('common-tags');
const Command = require('../base');
const Discord = require('discord.js');

module.exports = class ListGroupsCommand extends Command {
	constructor(client) {
		super(client, {
			name: 'groups',
			aliases: ['list-groups', 'show-groups','gruplar'],
			group: 'commands',
			memberName: 'groups',
			description: 'GrublarÄ± listeler.',
			details: 'Bu komutu sadece adminler kullana bilir..',
			guarded: true
		});
	}

	hasPermission(msg) {
		if(!msg.guild) return this.client.isOwner(msg.author);
		return msg.member.hasPermission('ADMINISTRATOR') || this.client.isOwner(msg.author);
	}

	run(msg) {
		const gruplar = new Discord.RichEmbed()
	   .setColor('RANDOM')
	   .setTitle('__**Grublar**__')
	   .setDescription(stripIndents`${this.client.registry.groups.map(grp =>
				`**${grp.name}:** ${grp.isEnabledIn(msg.guild) ? 'Aktif' : 'Pasif'}`
			).join('\n')}
		`)
		return msg.channel.send(gruplar);
	}
};
