const { oneLine } = require('common-tags');
const Command = require('../base');

module.exports = class PingCommand extends Command {
	constructor(client) {
		super(client, {
			name: 'ping',
			group: 'util',
			memberName: 'ping',
			description: 'Bot pingi.',
			throttling: {
				usages: 5,
				duration: 10
			}
		});
	}

	async run(msg) {

		if(!msg.editable) {
			const pingMsg = await msg.reply('Hesaplanıyor...');
			return pingMsg.edit({embed: {
  color: 3447003,
  description: `Gecikme: ${pingMsg.createdTimestamp - msg.createdTimestamp}ms Pingim: ${Math.round(this.client.ping)}ms.`
}});
		} else {
			await msg.edit('Hesaplanıyor...');
			return msg.edit({embed: {
  color: 3447003,
  description: `Gecikme: ${pingMsg.createdTimestamp - msg.createdTimestamp}ms Pingim: ${Math.round(this.client.ping)}ms.`
}});
		}
	}
};
