const { stripIndents, oneLine } = require('common-tags');
const Command = require('../base');

module.exports = class PrefixCommand extends Command {
	constructor(client) {
		super(client, {
			name: 'prefix',
			group: 'util',
			memberName: 'prefix',
			description: 'Botun prefixsini atar.',
			format: '[prefix/"default"/"none"]',
			details: oneLine`
Botun komutlarını kullanmak için botun prefixni atar.
			`,
			examples: ['prefix', 'prefix -', 'prefix omg!', 'prefix default', 'prefix none'],

			args: [
				{
					key: 'prefix',
					prompt: 'Botun prefini ne yapmak istersiniz?',
					type: 'string',
					max: 15,
					default: ''
				}
			]
		});
	}

	async run(msg, args) {
		// Just output the prefix
		if(!args.prefix) {
			const prefix = msg.guild ? msg.guild.commandPrefix : this.client.commandPrefix;
			return msg.reply(stripIndents`
				${prefix ? `Prefix \`${prefix}\`` : 'Prefix Yok.'}
				Komutları kullanmak için ${msg.anyUsage('komut')}.
			`);
		}

		// Check the user's permission before changing anything
		if(msg.guild) {
			if(!msg.member.hasPermission('ADMINISTRATOR') && !this.client.isOwner(msg.author)) {
				return msg.reply('Yanlızca adminler değiştire bilir.');
			}
		} else if(!this.client.isOwner(msg.author)) {
			return msg.reply('Yanlızca bot sahibi prefixi değiştire bilir.');
		}

		// Save the prefix
		const lowercase = args.prefix.toLowerCase();
		const prefix = lowercase === 'none' ? '' : args.prefix;
		let response;
		if(lowercase === 'varsayılan') {
			if(msg.guild) msg.guild.commandPrefix = null; else this.client.commandPrefix = null;
			const current = this.client.commandPrefix ? `\`${this.client.commandPrefix}\`` : 'Prefix yok';
			response = `Prefix varsayılana çevrildi (varsayılan prefix ${current}).`;
		} else {
			if(msg.guild) msg.guild.commandPrefix = prefix; else this.client.commandPrefix = prefix;
			response = prefix ? `Prefix değiştirildi yeni prefix: \`${args.prefix}\`` : 'Prefix tamamen kaldırıldı.';
		}

		await msg.channel.send({embed: {
  color: 3447003,
  description: `**${response}\n Komutları kullanmak için ${msg.anyUsage('komut')}.**`
}});
		return null;
	}
};
